// Assuming the incoming message object is stored in the variable 'msg'
// You can access the input set of strings and the given set of strings using msg.payload
const givenName = env.get("Area_Name");
const givenName2 = env.get("Area_Name2");
try {
    // Check if the required fields are present in the payload
    if (!msg.payload) {
        throw new Error("Invalid input payload");
    }

    // Convert sets to arrays for easier comparison
    const inputSet = Array.isArray(msg.payload) ? msg.payload : [];
    //const givenSet = Array.isArray(TrafficLights) ? msg.payload.givenSet : [];

    // Compare sets and create a list of booleans
    const result = inputSet.includes(givenName) || inputSet.includes(givenName2);

    // Create the output message with the list of booleans
    msg.payload = result;

    // Return the modified message object
    return msg;

} catch (error) {
    // Handle errors
    msg.payload = {
        error: error.message || "Error comparing sets"
    };

    // Return the error message
    return msg;
}

